// Vikram Anantha
// Jul 3 2023
// Columbia Project Summer 2023
// Tutorial from: https://www.youtube.com/watch?v=1_WXNvK_tjs

import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import { StyleSheet, Text, View, Image, Button } from 'react-native';



const UpperImage = () => {
  return (
    <View style={styles.upperpart}>
      <Image
        source={{
          uri: 'https://food.fnr.sndimg.com/content/dam/images/food/fullset/2018/5/16/0/FNK_WATERMELON-PIG-H_s4x3.jpg.rend.hgtvcom.406.406.suffix/1526483893553.jpeg'
        }}
        style={{width: 200, height:200}}
      />
    </View>
  )
}

const LowerText = props => {
  const [numPotatos, changeNumPotatos] = useState(3)
  return (
    <View style={styles.lowerpart}>
      <Text>I have {numPotatos} potatos</Text>
      <Button  style={styles.button}
        onPress = {() => {
          changeNumPotatos(numPotatos + 1);
        }}
        disabled = {numPotatos >= 10}
        title = {numPotatos < 10 ? "Get another potato" : "ok that's a lot chill a little"}
        // title = "Get another potato"
      />
    </View>
  )
}

const styles = StyleSheet.create({
  upperpart: {
    flex: 2,
    backgroundColor: 'powderblue',
    alignItems: 'center',
    justifyContent: 'center',
    // width:500
  },
  lowerpart: {
    flex: 3,
    backgroundColor: 'skyblue',
    alignItems: 'center',
    justifyContent: 'center',
    // width:500
  }
});

const App = () => {
  return (
    <View style={{flex: 1}}>
      {/* <Text>Wasssupppppp</Text> */}
      <UpperImage/>
      <LowerText/>
    </View>
  );
}


export default App
