// Vikram Anantha
// Jul 3 2023
// Columbia Project Summer 2023
// Tutorial from: https://www.youtube.com/watch?v=1_WXNvK_tjs

import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import { StyleSheet, Text, View, Image, Pressable, ScrollView, SafeAreaView } from 'react-native';

const App = () => {
  return (
    <SafeAreaView style={{flex: 1}}>
      <ScrollView style={{backgroundColor: 'pink',}}>
        <UpperImage/>
        <LowerText/>
      </ScrollView>
    </SafeAreaView>
  );
}

const UpperImage = () => {
  return (
    <View style={styles.upperpart}>
      <Image
        source={{
          uri: 'https://food.fnr.sndimg.com/content/dam/images/food/fullset/2018/5/16/0/FNK_WATERMELON-PIG-H_s4x3.jpg.rend.hgtvcom.406.406.suffix/1526483893553.jpeg'
        }}
        style={{width: 200, height:200}}
      />
    </View>
  )
}

const LowerText = props => {
  const [numPotatos, changeNumPotatos] = useState(3)
  const shouldDisable = numPotatos >= 10
  return (
    <View style={styles.lowerpart}>
      <View style={styles.lowertext}>
        <Text>I have {numPotatos} potatos</Text>
      </View>
      <View style={styles.lowerbutton}>
        {/* <Button style={styles.button} 
          onPress = {() => {
            changeNumPotatos(numPotatos + 1);
          }}
          disabled = {numPotatos >= 10}
          title = {numPotatos < 10 ? "Get another potato" : "ok that's a lot chill a little"}
          // title = "Get another potato"
        /> */}
        <Pressable 
          style={ ({pressed}) => [
            {
              backgroundColor: pressed ? '#2f5aa1' : 'blue', 
              // borderWidth: pressed ? 3 : 0,
              // height: 
            },
            !shouldDisable ? styles.button : styles.disabledbutton
          ]}
          onPress = {() => {
            changeNumPotatos(numPotatos + 1);
          }}
          disabled = {shouldDisable}
        >
          <Text style={!shouldDisable ? styles.buttontext : styles.disabledbuttontext} >
            {!shouldDisable ? "Get another potato" : "ok that's a lot chill a little"}
          </Text>
        </Pressable>
      </View>
    </View>
  )
}


const styles = StyleSheet.create({
  upperpart: {
    flex: 2,
    backgroundColor: 'powderblue',
    alignItems: 'center',
    justifyContent: 'center',
    // width:500
  },
  lowertext: {
    flex: 4,
    alignItems: 'center',
    justifyContent: 'center',
  },
  lowerbutton: {
    flex: 1,
    // fontFamily: 'sans-serif-condensed',

  },
  lowerpart: {
    flex: 3,
    backgroundColor: 'skyblue',
    alignItems: 'center',
    justifyContent: 'center',
    // width:500
  },
  button: {
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 12,
    paddingHorizontal: 32,
    borderRadius: 4,
    elevation: 3,
    // backgroundColor: 'blue',
    borderRadius: 30,
    // borderWidth: 2,
  },
  disabledbutton: {
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 12,
    paddingHorizontal: 32,
    borderRadius: 4,
    elevation: 3,
    backgroundColor: 'grey',
    borderRadius: 30,
  },
  disabledbuttontext: {
    color: "white",
    fontFamily: 'sans-serif',
    fontWeight: 'bold',
    // fontVariant: 'italics'
  },
  buttontext: {
    color: "white",
    fontFamily: 'sans-serif',
    fontWeight: 'bold',
    // fontVariant: 'italics'
  },
});


export default App
