// Vikram Anantha
// Columbia Summer Internship 2023
// Mentored under Prof Sharon Di, Yongjie Fu

// This is Version 1 of the Pedestrian App

import Paho from "paho-mqtt";

import { useState, useEffect } from "react";
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, Button, View, Vibration, Platform, Dimensions } from 'react-native';
import { Svg, Circle, Line } from 'react-native-svg';

var client
var ped_id = parseInt(Math.random() * 100);
const coords_interval_ms = 1000
const receivetopic = "ngi/1/detection/fromserver/" + ped_id
const sendtopic = "ngi/1/detection/toserver"
var temp_coords_x = 0
var temp_coords_y = 0
var vehicleData = []
const vibration_pattern = {
  'android': [
    0, 500, 250, 250, 250 // wait 0s, vibrate .5s, wait .25s, vibrate .25s, wait .25s
  ],
  'ios': [
    0, 250 // wait 0s, vibrate, wait .25s
  ]
}
const alive_chart = {
  true: {
    text: "So far so good",
    color: "#47c906"
  },
  false: {
    text: "DANGER DANGER COLLISION INCOMING",
    color: "#c90606"
  }
}

const randomNumber = () => {
  const generateRandomColor = Math.floor(Math.random() * 16777215)
      .toString(16)
      .padStart(6, '0');
  // return `#${generateRandomColor}`;
  return '#000000'
}

const ScatterPlot = props => {

  const width = Math.min(props.width, props.height)
  const height = Math.min(props.width, props.height)
  const gridSize = width / 10;
  const scale = width / 1000;
  // console.log(props.data)
  // console.log(height)
  var colors = []
  props.data.forEach((_, ind) => {
    colors.push(randomNumber())
  })
  return (
      <Svg style={styles.graph}>


        {Array.from(Array(11)).map((_, index) => (
          <Line
            key={`vertical-${index}`}
            x1={index * gridSize}
            y1={0}
            x2={index * gridSize}
            y2={height} // Adjust this based on your desired graph height
            stroke="gray"
            strokeWidth="0.25"
          />
        ))}


        {Array.from(Array(11)).map((_, index) => (
          <Line
            key={`horizontal-${index}`}
            x1={0}
            y1={index * gridSize}
            x2={width} // Adjust this based on your desired graph width
            y2={index * gridSize}
            stroke="gray"
            strokeWidth="0.25"
          />
        ))}


        {props.data.map((vehicle, dataindex) => (
          vehicle.map((point, index) => (
            <Circle
            key={index}
            cx={(point.x * scale)}
            cy={(point.y * scale)}
            r={1}
            fill={colors[dataindex]}
          />
          ))

        ))}
      </Svg>
  );
}


export default function App() {

  const [good, setalive] = useState(true);
  const [coordsx, updateCoordsX] = useState(0)
  const [coordsy, updateCoordsY] = useState(0)

  const [graphWidth, changeGraphWidth] = useState(Dimensions.get("window").height)
  const [graphHeight, changeGraphHeight] = useState(Dimensions.get("window").height)

  function receive(msg) { 
    if (msg.destinationName === receivetopic) {

    // console.log(msg)
    var message = "";
    try {
      // Parse the JSON string into a JavaScript object
      message = JSON.parse(msg.payloadString);
    } catch (error) {
      console.error('Error parsing JSON:', error);
    }
    console.log(message)

    if (message['status'] === "bad") {
      Vibration.vibrate(vibration_pattern[Platform.OS], true)      
      // console.log("BAD")
      setalive(false)
    }
    else if (message['status'] === "good") {
      Vibration.cancel()
      // console.log("GOOD")
      setalive(true)
    }
    else if (message['status'] === 'info') {
      // vehicleData.push([message['ped']['future']['x'], message['ped']['future']['y']])
      // vehicleData.push([message['veh']['future']['x'], message['veh']['future']['y']])
      message['future']['ped'].forEach((_, ind) => {
        vehicleData.push(message['future']['ped'][ind])
      })
      message['future']['veh'].forEach((_, ind) => {
        vehicleData.push(message['future']['veh'][ind])
      })
    }
    // console.log(message)
  }}

  useEffect(() => {
    client = new Paho.Client(
      "broker.mqttdashboard.com",
      Number(8000),
      `python-mqtt-${ped_id}`
    );
    client.connect( 
      {
        onSuccess: () => { 
        console.log("Connected!");
        client.subscribe(receivetopic);
        client.onMessageArrived = receive;
      },
      onFailure: () => {
        console.log("Failed to connect!"); 
      }
      }
    );
    const interval = setInterval(() => {
      // console.log(temp_coords_x + "," + temp_coords_y) //asdf
      var curtime = Date.now()/1000
      // console.log(curtime + ", " + temp_coords_x)
      send_coords(client, temp_coords_x, temp_coords_y, curtime)
      // updateCoords({'x': coords.x+1, 'y': coords.y-0.4})
      temp_coords_x += 5.3
      temp_coords_y += 8.4
      temp_coords_x = Math.round(temp_coords_x*100)/100
      temp_coords_y = Math.round(temp_coords_y*100)/100
      updateCoordsX(temp_coords_x)
      updateCoordsY(temp_coords_y)
      // 
    }, coords_interval_ms);
    return () => clearInterval(interval);
  }, [])

  function send_coords(c, coordsx, coordsy, time) {
    const message = new Paho.Message((ped_id + "," + coordsx + "," + coordsy + ',' + time).toString());
    message.destinationName = sendtopic;

    c.send(message);
  }

  return (
    <View style={styles.container}>
      <View style={{height: '40%', alignItems: 'center', justifyContent: 'center',}}>
        <Text style={{color: alive_chart[good]['color'], fontSize: 50}}>
          {alive_chart[good]['text']}
        </Text>
      </View>
      <View style={{height: '50%', alignItems: 'center', justifyContent: 'center', backgroundColor: '#ffffaa'}} onLayout={(event) => {
      var {x, y, width, height} = event.nativeEvent.layout;
      console.log(x)
      console.log(y)
      changeGraphHeight(height)
      changeGraphWidth(width)}}>
        <ScatterPlot 
          gridSize={10}
          futuredata={vehicleData}
          realtimedata
          width={graphWidth}
          height={graphHeight}
        />
        <Text>Real Time Graph of Trajectories</Text>
      </View>
      <View style={{height: '10%', alignItems: 'center', justifyContent: 'center',}}>
        <Text>
          Your Coords: ({coordsx}, {coordsy})
        </Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  graph: {
    flex: 1,
  },
});
